function playerCard(){$(".player").each(function(){var a=$(this),t=a.text(),i=a.data("height"),l=a.data("weight"),r=a.data("place");console.log(t,i,r);var e='<div class="player-card animated zoomIn"><img src="images/'+t+'.jpg" alt=""><h4>'+t+"</h4><ul><li><strong>Height:</strong> "+i+"</li><li><strong>Weight:</strong> "+l+"</li><li><strong>Birthplace:</strong> "+r+"</li></ul></div>";a.append(e)})}$(function(){playerCard()});